import React, { useMemo, useState } from 'react';
import { motion } from 'framer-motion';

type Office = {
  name: string;
  address: string[];
  lat?: number;
  lng?: number;
};

const OFFICES: Office[] = [
  { name: 'Global HQ', address: ['12 Spintex Road', 'Accra, Ghana'], lat: 5.629, lng: -0.145 },
  { name: 'Nairobi Studio', address: ['Ngong Road, Kilimani', 'Nairobi, Kenya'], lat: -1.292, lng: 36.8219 },
  { name: 'Cape Town Node', address: ['Loop Street, City Centre', 'Cape Town, South Africa'], lat: -33.9249, lng: 18.4241 },
];

const fadeUp = {
  hidden: { opacity: 0, y: 16 },
  visible: (i = 0) => ({ opacity: 1, y: 0, transition: { delay: i * 0.06, duration: 0.4, ease: 'easeOut' } }),
};

const Contact: React.FC = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [topic, setTopic] = useState('support');
  const [message, setMessage] = useState('');
  const [sending, setSending] = useState(false);
  const [sent, setSent] = useState<null | 'ok' | 'err'>(null);

  const disabled = useMemo(() => !name || !email || message.trim().length < 10 || sending, [name, email, message, sending]);

  async function onSubmit(e: React.FormEvent) {
    e.preventDefault();
    if (disabled) return;
    try {
      setSending(true);
      // Demo submit: POST to a no-op endpoint so the UI feels real
      await fetch('/api/ping', { method: 'POST', body: JSON.stringify({ name, email, topic, message }), headers: { 'Content-Type': 'application/json' } }).catch(() => {});
      setSent('ok');
      setName('');
      setEmail('');
      setTopic('support');
      setMessage('');
    } catch (err) {
      setSent('err');
    } finally {
      setSending(false);
      setTimeout(() => setSent(null), 4000);
    }
  }

  return (
    <div className="min-h-screen bg-[var(--bg)] text-[var(--text)]">
      <header className="border-b border-[color:var(--border)]">
        <div className="mx-auto max-w-7xl px-4 sm:px-6 py-6 flex items-center justify-between">
          <a href="/" className="font-extrabold tracking-tight text-xl">
            UBU <span className="text-[var(--orange-600)]">Lite</span>
          </a>
          <nav className="flex items-center gap-4 text-sm">
            <a className="hover:text-[var(--navy-800)]" href="/faq">FAQ</a>
            <a className="hover:text-[var(--navy-800)]" href="/privacy">Privacy</a>
            <a className="hover:text-[var(--navy-800)]" href="/terms">Terms</a>
          </nav>
        </div>
      </header>

      <main className="mx-auto max-w-7xl px-4 sm:px-6 py-10">
        <motion.h1 initial="hidden" animate="visible" variants={fadeUp} className="text-3xl sm:text-4xl font-extrabold tracking-tight">
          Contact us
        </motion.h1>
        <motion.p custom={1} initial="hidden" animate="visible" variants={fadeUp} className="mt-2 text-[var(--muted)] max-w-2xl">
          Reach our team for support, partnerships, or press. We usually reply within one business day.
        </motion.p>

        {/* Grid: Contact Cards + Form */}
        <div className="mt-10 grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Cards */}
          <div className="space-y-4 order-2 lg:order-1 lg:col-span-1">
            {[
              { title: 'Customer Support', byline: 'Help center and ticketing', cta: 'Visit Help Center', href: '/faq' },
              { title: 'Enterprise', byline: 'Custom solutions for teams', cta: 'Talk to sales', href: '/book' },
              { title: 'Press', byline: 'Media and speaking', cta: 'press@ubulite.com', href: 'mailto:press@ubulite.com' },
            ].map((c, i) => (
              <motion.a
                key={c.title}
                custom={i}
                initial="hidden"
                animate="visible"
                variants={fadeUp}
                href={c.href}
                className="block rounded-2xl border border-[color:var(--border)] bg-[var(--card)] p-5 hover:shadow-sm transition-shadow"
              >
                <div className="font-semibold">{c.title}</div>
                <div className="text-[var(--muted)] text-sm">{c.byline}</div>
                <div className="mt-2 text-[var(--accent-strong)] text-sm">{c.cta} →</div>
              </motion.a>
            ))}
          </div>

          {/* Form */}
          <motion.form onSubmit={onSubmit} initial="hidden" animate="visible" variants={fadeUp} className="order-1 lg:order-2 lg:col-span-2 rounded-2xl border border-[color:var(--border)] bg-[var(--card)] p-6">
            <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
              <div>
                <label className="block text-sm mb-1">Name</label>
                <input value={name} onChange={(e) => setName(e.target.value)} className="w-full rounded-lg border border-[color:var(--border)] bg-transparent px-3 py-2" placeholder="Your full name" />
              </div>
              <div>
                <label className="block text-sm mb-1">Email</label>
                <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} className="w-full rounded-lg border border-[color:var(--border)] bg-transparent px-3 py-2" placeholder="you@example.com" />
              </div>
              <div>
                <label className="block text-sm mb-1">Topic</label>
                <select value={topic} onChange={(e) => setTopic(e.target.value)} className="w-full rounded-lg border border-[color:var(--border)] bg-transparent px-3 py-2">
                  <option value="support">Support</option>
                  <option value="enterprise">Enterprise</option>
                  <option value="press">Press</option>
                  <option value="partnerships">Partnerships</option>
                </select>
              </div>
              <div className="sm:col-span-2">
                <label className="block text-sm mb-1">Message</label>
                <textarea value={message} onChange={(e) => setMessage(e.target.value)} rows={6} className="w-full rounded-lg border border-[color:var(--border)] bg-transparent px-3 py-2" placeholder="How can we help?" />
              </div>
            </div>
            <div className="mt-4 flex items-center gap-3">
              <button disabled={disabled} className={`px-4 py-2 rounded-lg text-white ${disabled ? 'bg-[color:var(--muted)] cursor-not-allowed' : 'bg-[var(--accent-strong)] hover:bg-[var(--accent-deep)]'}`}>
                {sending ? 'Sending…' : 'Send message'}
              </button>
              {sent === 'ok' && <span className="text-green-600 text-sm">Thanks! We’ll be in touch shortly.</span>}
              {sent === 'err' && <span className="text-red-600 text-sm">Something went wrong. Try again.</span>}
            </div>
          </motion.form>
        </div>

        {/* Offices */}
        <section className="mt-12">
          <h2 className="text-2xl font-semibold">Our Offices</h2>
          <div className="mt-4 grid grid-cols-1 md:grid-cols-2 gap-5">
