import React, { useEffect } from 'react';

type Props = { message: string; onClose: () => void; duration?: number };

const Toast: React.FC<Props> = ({ message, onClose, duration = 3000 }) => {
  useEffect(() => {
    const t = setTimeout(onClose, duration);
    return () => clearTimeout(t);
  }, [onClose, duration]);
  return (
    <div role="status" aria-live="polite" className="fixed bottom-4 left-1/2 -translate-x-1/2 z-50">
      <div className="rounded-full bg-[var(--card)] border border-[color:var(--border)] px-4 py-2 shadow">
        {message}
      </div>
    </div>
  );
};

export default Toast;
